import { drizzleReactHooks } from '@drizzle/react-plugin';
import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';

const { useDrizzle, useDrizzleState } = drizzleReactHooks;

const TokenCompanyswitch = ({ traductores, longitud }) => {
  const { useCacheCall } = useDrizzle();
  const { drizzle } = useDrizzle();
  const drizzleState = useDrizzleState(state => state);

  const [selectedOption, setSelectedOption] = useState('');
  const [selectedData2Option1, setSelectedData2Option1] = useState('');
  const [inputNumber, setInputNumber] = useState('');
  const [isFormFilled, setIsFormFilled] = useState(false);

  const address = drizzleState.accounts[0];

  const handleChangeOption1 = (event) => {
    const selectedIndex = event.target.selectedIndex;
    setSelectedOption('option1');
    setSelectedData2Option1(traductores[selectedIndex - 1]?.data2 || '');
  };

  // Fetch the required data using useEffect
  useEffect(() => {
    const fetchData = async () => {
      if (selectedData2Option1) {
        // Use try-catch block to handle errors
        try {
          const tokens = await useCacheCall("GovernanceToken", "balanceOf", selectedData2Option1);
          // Update state with fetched data
          setIsFormFilled(!!tokens); // Convert tokens to a boolean value
        } catch (error) {
          // Handle error if necessary
          setIsFormFilled(false); // Set to false if there's an error fetching data
        }
      } else {
        setIsFormFilled(false);
      }
    };

    fetchData();
  }, [selectedData2Option1]);

  const handleSubmit = (ev) => {
    ev.preventDefault();
    console.log('Formulario enviado para la opción 1');
    // The isFormFilled state should be true at this point

    //Eliminar Compañia
    const stackId = drizzle.contracts.Traductores.methods.BurnCompany.cacheSend(selectedData2Option1);
  };

  const handleInputChange = (event) => {
    const value = event.target.value;
    if (Number(value) >= 0 || value === '') {
      setInputNumber(value);
    }
  };

  return (
    <div>
      <div>
        <p></p>
        <form>
          <label>
            Selecciona empresa
            <select value={selectedData2Option1} onChange={handleChangeOption1}>
              <option value="">Seleccionar</option>
              {Array.from({ length: longitud }, (_, i) => (
                <option key={i} value={traductores[i]?.data2}>
                  {traductores[i]?.data1} {/* Aquí se muestra el nombre del traductor */}
                </option>
              ))}
            </select>
          </label>
          <button onClick={handleSubmit} disabled={!isFormFilled}>
            Eliminar
          </button>
        </form>
      </div>
    </div>
  );
};

export default TokenCompanyswitch;
