import { drizzleReactHooks } from '@drizzle/react-plugin';
import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';

const { useDrizzle } = drizzleReactHooks;

const DaoTraductorRow = ({ TraductorIndex, onDataChange }) => {
  const { useCacheCall } = useDrizzle();
  const [addr, setAddr] = useState(null);
  const [datos, setDatos] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      const { addr, datos } = await useCacheCall(['Traductores'], (call) => {
        const addr = call('Traductores', 's_traductores', TraductorIndex);
        const datos = addr && call('Traductores', 'datosHolders', addr);
        return { addr, datos };
      });

      setAddr(addr);
      setDatos(datos);
    };

    fetchData();
  }, [TraductorIndex]);

  useEffect(() => {
    if (datos && addr) {
      onDataChange(TraductorIndex, datos, addr);
    }
  }, [TraductorIndex, datos, addr, onDataChange]);

  return (
    <tr key={'TRA-' + TraductorIndex}>
      <td>{datos}</td>
    </tr>
  );
};

export default DaoTraductorRow;
