import React, { useState } from 'react';
import { drizzleReactHooks } from '@drizzle/react-plugin';
import { useParams, Link } from 'react-router-dom';

const { useDrizzle, useDrizzleState } = drizzleReactHooks;

const Daopropose = ({ children }) => {
  const { useCacheCall, useCacheSend } = useDrizzle();
  const drizzleState = useDrizzleState((state) => state);

  const addr = drizzleState.accounts[0];
  const balance = drizzleState.accountBalances[addr];

  // Create an array of 4 options
  const options = ['Option 1', 'Option 2', 'Option 3', 'Option 4'];

  const [selectedOption, setSelectedOption] = useState(null);
  const [selectedNumber, setSelectedNumber] = useState(1);
  const [positiveNumber, setPositiveNumber] = useState(0);
  const [textInput, setTextInput] = useState('');

  const handleOptionChange = (option) => {
    setSelectedOption(option);
    setSelectedNumber(1); // Reset the selectedNumber when a new option is selected
    setPositiveNumber(0); // Reset the positiveNumber when a new option is selected
    setTextInput(''); // Reset the textInput when a new option is selected
  };

  const handleNumberChange = (event) => {
    const value = parseInt(event.target.value);
    setSelectedNumber(value >= 1 ? value : 1);
  };

  const handlePositiveNumberChange = (event) => {
    const value = parseInt(event.target.value);
    setPositiveNumber(value >= 0 ? value : 0);
  };

  const handleTextChange = (event) => {
    setTextInput(event.target.value);
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    // Do something with the form data, like submitting it to the backend
    // For example, you can access the selectedOption, selectedNumber, positiveNumber, and textInput here.
    console.log('Selected Option:', selectedOption);
    console.log('Selected Number:', selectedNumber);
    console.log('Positive Number:', positiveNumber);
    console.log('Text Input:', textInput);
  };

  return (
    <>
      <h3>Votar {selectedOption || 'Seleccione una opciÃ³n'}</h3>
      <form onSubmit={handleSubmit}>
        <table>
          <thead>
            <tr>
              <th>Option</th>
              <th>Vote Count</th>
              {selectedOption === 'Option 2' || selectedOption === 'Option 3' ? (
                <>
                  <th>Positive Number</th>
                  <th>Text Input</th>
                </>
              ) : null}
            </tr>
          </thead>
          <tbody>
            {options.map((option, i) => (
              <tr key={i}>
                <td>
                  <label>
                    <input
                      type="radio"
                      value={option}
                      checked={selectedOption === option}
                      onChange={() => handleOptionChange(option)}
                    />
                    {option}
                  </label>
                </td>
                <td>
                  {selectedOption === option && ( // Only show the input fields for selected options
                    <>
                      <input
                        type="number"
                        value={selectedNumber}
                        onChange={handleNumberChange}
                        min="1"
                      />
                      {selectedOption === 'Option 2' || selectedOption === 'Option 3' ? (
                        <>
                          <input
                            type="number"
                            value={positiveNumber}
                            onChange={handlePositiveNumberChange}
                            min="0"
                          />
                          <input
                            type="text"
                            value={textInput}
                            onChange={handleTextChange}
                          />
                        </>
                      ) : null}
                    </>
                  )}
                </td>
              </tr>
            ))}
          </tbody>
        </table>
        {selectedOption ? (
          <button type="submit">Submit</button>
        ) : (
          <p>Select an option before submitting</p>
        )}
      </form>
    </>
  );
};

export default Daopropose;
