import React, { useState, useEffect } from "react";
import { drizzleReactHooks } from '@drizzle/react-plugin';

const { useDrizzle, useDrizzleState, useCacheCall } = drizzleReactHooks;

const IPFS_BASE_URL = "https://ipfs.io/ipfs/";

const Oneipfs = ({ nftsid , address}) => {
  const { drizzle } = useDrizzle();
  const drizzleState = useDrizzleState(state => state);
  const addr = drizzleState.accounts[0];
  const [isLoading, setIsLoading] = useState(true); // Estado de carga
  let data = useCacheCall("NFTContract", "_nftMetadata", nftsid);

  useEffect(() => {
    const loadDataInterval = setInterval(() => {
      data = useCacheCall("NFTContract", "_nftMetadata", nftsid);

      if (data) {
        setIsLoading(false); // Si se obtienen datos con éxito, detén la carga
        clearInterval(loadDataInterval); // Detener el intervalo
      }
    }, 1000); // Consultar cada segundo

    return () => {
      clearInterval(loadDataInterval); // Limpia el intervalo si el componente se desmonta
    };
  }, [nftsid]);

  console.log("ID NFT", nftsid);
  console.log("Address:", address);

  const renderContent = () => {
    if (isLoading) {
      return <p>Loading...</p>;
    }

    switch (data.format) {
      case "0":
        return (
          <>
            <img
              src={IPFS_BASE_URL + data.cid}
              alt="Imagen IPFS"
              style={{ width: "200px", height: "200px" }}
            />
            {/* Resto del contenido */}
          </>
        );
      case "1":
        return (
          <>
            <video
              controls
              src={IPFS_BASE_URL + data.cid}
              style={{ width: "200px", height: "200px" }}
            />
            {/* Resto del contenido */}
          </>
        );
      case "2":
        return (
          <>
            <audio
              controls
              src={IPFS_BASE_URL + data.cid}
            />
            {/* Resto del contenido */}
          </>
        );
      default:
        return <p>Formato desconocido</p>;
    }
  };

  return (
    <>
      {renderContent()}
      {addr !== address && (
        <button
          key="submit"
          className="pure-button"
          type="button"
          onClick={ev => {
            ev.preventDefault();

            const stackId = drizzle.contracts.NFTContract.methods.exchangeNFTOther.cacheSend(Number(nftsid), addr);
          }}
        >
          Comprar
        </button>
      )}
    </>
  );
};

export default Oneipfs;
