import { create } from 'ipfs-http-client';
import React, { useState } from "react";

import Dropzone from "react-dropzone";
import axios from "axios";

import { drizzleReactHooks } from '@drizzle/react-plugin'
const { useDrizzle } = drizzleReactHooks;

const ipfs = create({ host: "localhost", port: 5001, protocol: "http" });

const Ipfsreact = () => {
  const { useCacheCall } = useDrizzle();

  const [hash, setHash] = useState("");
  const [fileBuffer, setFileBuffer] = useState(null);
  const [previewURL, setPreviewURL] = useState("");

  const handleDrop = async (acceptedFiles) => {
    try {
      const file = acceptedFiles[0];
      const reader = new FileReader();
      reader.readAsArrayBuffer(file);
      reader.onloadend = async () => {
        const buffer = Buffer(reader.result);
        setFileBuffer(buffer);
        // Mostrar la vista previa de la imagen
        setPreviewURL(URL.createObjectURL(file));
      };
    } catch (error) {
      console.error("Error reading the file:", error);
    }
  };

  const uploadToIPFS = async () => {
    try {
      const { cid } = await ipfs.add(fileBuffer);
      setHash(cid.toString());
    } catch (error) {
      console.error("Error uploading to IPFS:", error);
    }
  };

  const getFileFromIPFS = async () => {
    try {
      if (!hash) return;

      const response = await axios.get(`https://ipfs.io/ipfs/${hash}`);
      console.log("File content from IPFS:", response.data);
    } catch (error) {
      console.error("Error fetching file from IPFS:", error);
    }
  };

  return (
    <tbody>
      <div className="App">
        <h1>Subir Videos a IPFS</h1>
        <Dropzone onDrop={handleDrop}>
          {({ getRootProps, getInputProps }) => (
            <div {...getRootProps({ className: "dropzone" })} style={{
              backgroundColor: 'blue',
              border: '2px dashed white',
              borderRadius: '5px',
              padding: '20px',
              textAlign: 'center',
              color: 'white',
              cursor: 'pointer',
            }}>
              <input {...getInputProps()} />
              <p>Drag and drop a file here, or click to select a file</p>
              {previewURL && <img src={previewURL} alt="Preview" style={{ maxWidth: '100%', marginTop: '10px' }} />}
            </div>
          )}
        </Dropzone>
        <button onClick={uploadToIPFS} disabled={!fileBuffer}>
          Subir Video
        </button>
        <div>
          {hash && (
            <div>
              <h2>IPFS Hash:</h2>
              <p>{hash}</p>
              <button onClick={getFileFromIPFS}>Get File from IPFS</button>
            </div>
          )}
        </div>
      </div>
    </tbody>
  );
};

export default Ipfsreact;
