import React, { useEffect, useState } from "react";
import { drizzleReactHooks } from '@drizzle/react-plugin';

const { useDrizzle, useDrizzleState } = drizzleReactHooks;

const IPFS_BASE_URL = "https://ipfs.io/ipfs/";

const Oneipfs = ({ nftsid, address }) => {
  const { useCacheCall } = useDrizzle();
  const { drizzle } = useDrizzle();
  const drizzleState = useDrizzleState(state => state);
  const addr = drizzleState.accounts[0];
  const [isLoading, setIsLoading] = useState(true);
  const [data, setData] = useState(null);

  const fetchData = async () => {
    try {
      const newData = await useCacheCall("NFTContract", "_nftMetadata", nftsid);
      if (newData && newData.cid) {
        setData(newData);
        setIsLoading(false);
      } else {
        setIsLoading(true);
      }
    } catch (error) {
      if (error.message.includes("504")) {
        // Si se produce un error 5004 Gateway Timeout, volver a pedir los datos.
        fetchData();
      } else {
        setIsLoading(true);
      }
    }
  };

  useEffect(() => {
    const interval = setInterval(fetchData, 10000); // Comprobar cada 10 segundos.
    fetchData(); // Realizar la primera comprobación al cargar el componente.

    return () => {
      clearInterval(interval); // Limpiar el intervalo al desmontar el componente.
    };
  }, [nftsid]);

  const renderContent = () => {
    if (isLoading) {
      return <p>Loading...</p>;
    }

    switch (data.format) {
      case "0":
        return (
          <>
            <img
              src={IPFS_BASE_URL + data.cid}
              alt="Imagen IPFS"
              style={{ width: "200px", height: "200px" }}
            />
            {/* ... Resto del contenido */}
          </>
        );
      case "1":
        return (
          <>
            <video
              controls
              src={IPFS_BASE_URL + data.cid}
              style={{ width: "200px", height: "200px" }}
            />
            {/* ... Resto del contenido */}
          </>
        );
      case "2":
        return (
          <>
            <audio
              controls
              src={IPFS_BASE_URL + data.cid}
            />
            {/* ... Resto del contenido */}
          </>
        );
      default:
        return <p>Formato desconocido</p>;
    }
  };

  return (
    <>
      {renderContent()}
      {addr !== address && (
        <button
          key="submit"
          className="pure-button"
          type="button"
          onClick={ev => {
            ev.preventDefault();

            const stackId = drizzle.contracts.NFTContract.methods.exchangeNFTOther.cacheSend(Number(nftsid), addr);
          }}
        >
          Comprar
        </button>
      )}
    </>
  );
};

export default Oneipfs;
