import { drizzleReactHooks } from '@drizzle/react-plugin';
import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';

const { useDrizzle } = drizzleReactHooks;

const Daopropose2 = ({ traductores, longitud }) => {
  const { useCacheCall } = useDrizzle();

  const [selectedTraductor, setSelectedTraductor] = useState('');
  const [selectedData2, setSelectedData2] = useState('');

  const handleChange = (event) => {
    const selectedTraductor = event.target.value;
    setSelectedTraductor(selectedTraductor);

    // Buscar data2 del traductor seleccionado
    const selectedTraductorData = traductores.find((traductor) => traductor?.data1 === selectedTraductor);
    setSelectedData2(selectedTraductorData?.data2 || '');
  };

  return (
    <div>
      <form>
        <label>
          Selecciona un traductor:
          <select value={selectedTraductor} onChange={handleChange}>
            <option value="">Seleccionar</option>
            {Array.from({ length: longitud }, (_, i) => (
              <option key={i} value={traductores[i]?.data1}>
                {traductores[i]?.data1}
              </option>
            ))}
          </select>
        </label>
      </form>
      <p>data1 seleccionada: {selectedTraductor}</p>
      <p>data2 de la opci√≥n seleccionada: {selectedData2}</p>
    </div>
  );
};

export default Daopropose2;
