import React, { useState } from 'react';
import { drizzleReactHooks } from '@drizzle/react-plugin';
import { useParams, Link } from 'react-router-dom';

const { useDrizzle, useDrizzleState } = drizzleReactHooks;

const Daovotar = ({ children }) => {
  const { useCacheCall, useCacheSend } = useDrizzle(); // Agregamos useCacheSend para poder enviar transacciones
  const drizzleState = useDrizzleState((state) => state);
  let { index } = useParams();
  const addr = drizzleState.accounts[0];
  const balance = drizzleState.accountBalances[addr];

  // Creamos un estado local para almacenar la selección del usuario (1 o 0)
  const [voto, setVoto] = useState(null);

  // Función para manejar el envío del voto
  const handleVotoSubmit = (event) => {
    event.preventDefault();

    // Verificamos que se haya seleccionado una opción
    if (voto !== null) {
      // Enviamos la transacción con el voto a la blockchain
      // Aquí asumimos que tu contrato tiene una función "votar" que recibe el índice y el voto (1 o 0)
     // useCacheSend('TuContrato', 'votar', index, voto);
     console.log("Voto:", index);
     setVoto("");
    }
  };

  return (
    <>
      <h3>Votar {index}</h3>

      <form onSubmit={handleVotoSubmit}>
        <label>
          <input
            type="radio"
            value={1}
            checked={voto === 1}
            onChange={(e) => setVoto(parseInt(e.target.value))}
          />
          A favor
        </label>

        <label>
          <input
            type="radio"
            value={0}
            checked={voto === 0}
            onChange={(e) => setVoto(parseInt(e.target.value))}
          />
          En contra
        </label>

        <button type="submit">Enviar Voto</button>
      </form>
    </>
  );
};

export default Daovotar;
