import React, { useState } from 'react';
import { drizzleReactHooks } from '@drizzle/react-plugin';
import { useParams, Link } from 'react-router-dom';

const { useDrizzle, useDrizzleState } = drizzleReactHooks;

const Daopropose = ({ children }) => {
  const { useCacheCall, useCacheSend } = useDrizzle();
  const drizzleState = useDrizzleState((state) => state);

  const addr = drizzleState.accounts[0];
  const balance = drizzleState.accountBalances[addr];

  // Opciones de la encuesta
  const options = ['Opción 1', 'Opción 2', 'Opción 3', 'Opción 4'];

  // Estado para almacenar la opción seleccionada
  const [selectedOption, setSelectedOption] = useState(null);

  // Función para manejar el cambio de opción
  const handleOptionChange = (optionIndex) => {
    setSelectedOption(optionIndex);
  };

  return (
    <>
      <h3>Votar {index}</h3>

      {/* Mostrar las opciones de la encuesta */}
      <form>
        {options.map((option, index) => (
          <label key={index}>
            <input
              type="radio"
              value={index}
              checked={selectedOption === index}
              onChange={() => handleOptionChange(index)}
            />
            {option}
          </label>
        ))}
      </form>

      {/* Mostrar el botón para enviar la opción seleccionada */}
      <button onClick={() => handleVoteSubmit(selectedOption)}>Enviar Voto</button>
    </>
  );
};

export default Daopropose;
