import React, { useState } from 'react';

const DaoTraductorRow = ({ TraductorIndex, data, onDataChange }) => {
  const datosOptions = [1, 2, 3];

  const [selectedData, setSelectedData] = useState(data ? data : datosOptions[0]);

  const handleDataChange = (event) => {
    const newData = event.target.value;
    setSelectedData(newData);
    onDataChange(TraductorIndex, newData); // Actualiza el estado compartido en el componente padre
  };

  return (
    <tr key={'TRA-' + TraductorIndex}>
      <td>
        {datosOptions ? (
          <select value={selectedData} onChange={handleDataChange}>
            {datosOptions.map((option, index) => (
              <option key={index} value={option}>
                {option}
              </option>
            ))}
          </select>
        ) : (
          <input type="number" value={selectedData} onChange={handleDataChange} />
        )}
      </td>
    </tr>
  );
};

export default DaoTraductorRow;
