import { create } from 'ipfs-http-client';
import React, { useState } from "react";

import Dropzone from "react-dropzone";
import axios from "axios";





import { drizzleReactHooks } from '@drizzle/react-plugin'

const {useDrizzle, useDrizzleState} = drizzleReactHooks;

const ipfs = create({ host: "localhost", port: 5001, protocol: "http" });

const Ipfsreact = () => {
  const { useCacheCall } = useDrizzle();
  const { drizzle } = useDrizzle();
  const [setLastStackID] = useState(undefined)

  const drizzleState = useDrizzleState(state => state);

  const address = drizzleState.accounts[0];

  const [hash, setHash] = useState("");
  const [fileBuffer, setFileBuffer] = useState(null);
  const [previewURL, setPreviewURL] = useState("");
  const [imageName, setImageName] = useState("");
  const [description, setDescription] = useState("");
  const [price, setPrice] = useState("");
  let [direccion, setDireccion] = useState("");


  const handleDrop = async (acceptedFiles) => {
    try {
      const file = acceptedFiles[0];
      const reader = new FileReader();
      reader.readAsArrayBuffer(file);
      reader.onloadend = async () => {
        const buffer = Buffer(reader.result);
        setFileBuffer(buffer);
        // Mostrar la vista previa de la imagen
        setPreviewURL(URL.createObjectURL(file));
      };
    } catch (error) {
      console.error("Error reading the file:", error);
    }
  };

  const uploadToIPFS = async () => {
    try {
      const { cid } = await ipfs.add(fileBuffer);
      setHash(cid.toString());
      console.log("Valor de hash",hash);
      console.log("Valor de setHash",setHash);
    } catch (error) {
      console.error("Error uploading to IPFS:", error);
    }
  };

  const getFileFromIPFS = async () => {
    try {
      if (!hash) return;

      const response = await axios.get(`https://ipfs.io/ipfs/${hash}`);
      console.log("File content from IPFS:", response.data);
    } catch (error) {
      console.error("Error fetching file from IPFS:", error);
    }
  };


  //Función que se ejecuta cuando se rellena el formulario
  const handleSubmit = (event) => {
    event.preventDefault();
    // Asegurarse de que imageName tenga un valor predeterminado de "NFT" si está vacío.
    const nameToSubmit = imageName.trim() === "" ? "NFT" : imageName;

    // Asegurarse de que description tenga un valor predeterminado de "NFT de traducción" si está vacío.
    const descriptionToSubmit = description.trim() === "" ? "NFT de traducción" : description;    

    // Asegurarse de que price tenga un valor predeterminado de "1" si está vacío.
    const priceToSubmit = price.trim() === "" ? 1 : price;

    // Asegurarse de que direccion tenga un valor predeterminado de la persona que lo crea si está vacío.
    const addressToSubmit = direccion.trim() === "" ? address : direccion;    

    // Aquí puedes hacer lo que desees con los datos del formulario, por ejemplo, enviarlos a un contrato inteligente o a una API.
    console.log("Imagen:", nameToSubmit);
    console.log("Descripción:", descriptionToSubmit);
    console.log("Precio:", priceToSubmit);
    console.log("Dirección:", addressToSubmit);
    uploadToIPFS();
  // Si hash es una cadena vacía, se debe esperar hasta que se cargue un archivo y se obtenga un valor de hash antes de continuar.
  // Función auxiliar para esperar hasta que hash tenga un valor válido (no sea una cadena vacía)
  // Función auxiliar para esperar hasta que hash tenga un valor válido (no sea una cadena vacía)
  const waitHash = () => {
    return new Promise((resolve) => {
      if (hash !== "") {
        // En este punto, hash tiene un valor válido, así que podemos proceder con las operaciones relacionadas con el hash.
        console.log("CID:", hash);
        resolve();
      } else {
        console.log("Esperando a que se complete la carga a IPFS..., valor de hash",hash);
        setTimeout(() => resolve(waitHash()), 1000); // Esperar 1 segundo antes de verificar nuevamente
      }
    });
  };

  // Esperar hasta que hash tenga un valor válido (es decir, no sea una cadena vacía)
  waitHash().then(() => {
    // Llamar a la función createNFT con los datos del formulario y el valor del hash
    createNFT(nameToSubmit, hash, descriptionToSubmit, priceToSubmit, addressToSubmit);
  });
};

  const createNFT = async (nameToSubmit, hash, descriptionToSubmit, priceToSubmit, addressToSubmit) => {
    try {
      console.log("CID de createNFT:", hash);
      const stackId = drizzle.contracts.NFTContract.methods.createNFT.cacheSend(
        nameToSubmit,
        hash,
        descriptionToSubmit,
        priceToSubmit,
        addressToSubmit
      );
     // setLastStackID(stackId);
    } catch (error) {
      console.error("Error uploading to IPFS:", error);
    }
  };


  return (
    <tbody>
      <div className="App" style={{ display: "flex", flexDirection: "column", alignItems: "center" }}>
        <h1>Subir Videos a IPFS</h1>
        <Dropzone onDrop={handleDrop}>
          {({ getRootProps, getInputProps }) => (
            <div {...getRootProps({ className: "dropzone" })} style={{
              backgroundColor: 'blue',
              border: '2px dashed white',
              borderRadius: '5px',
              padding: '20px',
              textAlign: 'center',
              color: 'white',
              cursor: 'pointer',
            }}>
              <input {...getInputProps()} />
              <p>Arrastre y suelte un archivo aquí, o haga clic para seleccionarlo</p>
              {previewURL && <img src={previewURL} alt="Preview" style={{ maxWidth: '100%', marginTop: '10px' }} />}
            </div>
          )}
        </Dropzone>
        <div>
          {hash && (
            <div>
              <h2>IPFS Hash:</h2>
              <p>{hash}</p>
              <button onClick={getFileFromIPFS}>Conseguir archivo de IPFS</button>
            </div>
          )}
        </div>
        <div style={{ width: '100%' }}>
          <h2>Información de la imagen:</h2>
          <form onSubmit={handleSubmit} >
          <p> Nombre de la imagen: &nbsp;
              <input type="text" value={imageName} onChange={(e) => setImageName(e.target.value)} />
              </p>

              <p> 
              Descripción: &nbsp;
              <textarea value={description} onChange={(e) => setDescription(e.target.value)} />
            </p>

            <p> 
              Precio: &nbsp;
              <input type="number" value={price} onChange={(e) => setPrice(e.target.value)} />
              </p>

              <p> Dirección del creador: &nbsp;
                <input key="direccion" type="address" name="direccion" value={direccion} placeholder="0x0000"
                    onChange={ev => setDireccion(ev.target.value)} /> </p>
            <button type="submit" disabled={!fileBuffer}>Guardar información</button>
          </form>
        </div>
      </div>
    </tbody>
  );
};

export default Ipfsreact;
