import { drizzleReactHooks } from '@drizzle/react-plugin'



import {Link} from "react-router-dom";


const { useDrizzle, useDrizzleState } = drizzleReactHooks;

const Daowatch = ({DaoIndex}) => {

    const {useCacheCall} = useDrizzle();
    const drizzleState = useDrizzleState(state => state);

    const address = drizzleState.accounts[0];
    const balance = drizzleState.accountBalances[address];


    console.log("DaoIndex:", DaoIndex);

    const oz_states = ['Pending', 'Active', 'Canceled', 'Defeated',
    'Succeeded', 'Queued', 'Expired', 'Executed'];

/*
    const getProposalState = async (GovernorContract, DaoIndex) => {
        let propState = await gov.state(proposalId);
        console.log("Proposal State:", oz_states[propState.toNumber()]);
    }
*/
    /*

        // Proposal states as defined by OpenZeppelin/Compound

        // Helper function to get proposal state





     await getProposalState(govcontract, proposalId);  


    */

    let {addr, datos, propState} = useCacheCall(['GovernorContract'],
    call => {
    const addr = call("GovernorContract", "proposes", DaoIndex);
    const datos = addr && call("GovernorContract", "datosPropose", addr);
    const propState = addr && datos && call("GovernorContract","state",datos.id);
    return {addr, datos, propState};
    }
    );

    return <tr key={"Dao-" + DaoIndex}>
    <th>A<sub>{DaoIndex}</sub></th>
    <td>{datos?.description}</td>
    <td>{propState}</td>
    <td><Link to={`/dao/${DaoIndex}`}>Votar</Link></td>
    </tr>;


};
export default Daowatch;
