import React, { useState, useEffect } from 'react';
import { drizzleReactHooks } from '@drizzle/react-plugin';
import { useParams, Link } from 'react-router-dom';

import Daoform from "./Daoform";

const { useDrizzle, useDrizzleState } = drizzleReactHooks;

const Daopropose = ({ children }) => {
  const { useCacheCall, useCacheSend } = useDrizzle();
  const drizzleState = useDrizzleState((state) => state);

  const addr = drizzleState.accounts[0];
  const balance = drizzleState.accountBalances[addr];

  // Create an array of 4 options
  const options = ['Option 1', 'Option 2', 'Option 3', 'Option 4'];

  const [selectedOption, setSelectedOption] = useState(null);
  const [selectedNumber, setSelectedNumber] = useState(1);
  const [rows, setRows] = useState([]);

  // Fetch the data for "Traductores" on component mount
  useEffect(() => {
    const fetchData = async () => {
      const getTraductorLength = await useCacheCall("Traductores", "getTraductorLength");
      console.log("Traductores length:", getTraductorLength);

      const dataRows = [];
      for (let i = 0; i < getTraductorLength; i++) {
        const { addr, datos } = await useCacheCall(['Traductores'], call => {
          const addr = call("Traductores", "s_traductores", i); // Use i instead of TraductorIndex
          const datos = addr && call("Traductores", "datosHolders", addr);
          return { addr, datos };
        });
        dataRows.push(datos);
      }
      setRows(dataRows);
    };

    fetchData();
  }, [useCacheCall]);

  const handleOptionChange = (option) => {
    setSelectedOption(option);
    setSelectedNumber(1); // Reset the selectedNumber when a new option is selected
  };

  const handleNumberChange = (event) => {
    setSelectedNumber(event.target.value);
  };

  return (
    <>
      <h3>Votar {selectedOption || 'Seleccione una opciÃ³n'}</h3>
      <table>
        <thead>
          <tr>
            <th>Option</th>
            <th>Vote Count</th>
          </tr>
        </thead>
        <tbody>
          {options.map((option, i) => (
            <tr key={i}>
              <td>
                <label>
                  <input
                    type="radio"
                    value={option}
                    checked={selectedOption === option}
                    onChange={() => handleOptionChange(option)}
                  />
                  {option}
                </label>
              </td>
              <td>
                {selectedOption === option && option === 'Option 3' && ( // Only show the dropdown for Option 3
                  <select value={selectedNumber} onChange={handleNumberChange}>
                    <option value={1}>1</option>
                    <option value={2}>2</option>
                    <option value={3}>3</option>
                  </select>
                )}
                {selectedOption === option && option === 'Option 4' && ( // Only show the dropdown for Option 4
                  <select value={selectedNumber} onChange={handleNumberChange}>
                    <option value={1}>1</option>
                    <option value={2}>2</option>
                    <option value={3}>3</option>
                  </select>
                )}
              </td>
            </tr>
          ))}
        </tbody>
      </table>
      <Daoform Daooption={selectedOption}></Daoform>
    </>
  );
};

export default Daopropose;
