import {drizzleReactHooks} from '@drizzle/react-plugin'

const {useDrizzle, useDrizzleState} = drizzleReactHooks;

const IPFS_BASE_URL = "https://ipfs.io/ipfs/";


const Oneipfs = ({ nftsid }) => {
    const {useCacheCall} = useDrizzle();
    const drizzleState = useDrizzleState(state => state);


  let data = useCacheCall("NFTContract", "_nftMetadata", nftsid);
  console.log("data", data);
  //bucle
  //Otro componente
  //SAcar el CID, dado el numero
 // const admin = useCacheCall("NFTContract", "_nftMetadata", admin[0]);
 const IPFS_BASE_URL = "https://ipfs.io/ipfs/";


 return (
    <>
      {data && (
        <div style={{ display: "flex", flexDirection: "column", alignItems: "center" }}>
          <img
            src={IPFS_BASE_URL + data.cid}
            alt="Imagen IPFS"
            style={{ width: "200px", height: "200px" }}
          />
          <table>
            <tbody>
              <tr>
                <th>
                  A<sub>{nftsid}</sub>
                </th>
                <td>{data.cid}</td>
              </tr>
              <tr>
                <th>Creator</th>
                <td>{data.creator}</td>
              </tr>
            </tbody>
          </table>
        </div>
      )}
    </>
  );
};

export default Oneipfs;
