import React, { useState } from "react";
import { drizzleReactHooks } from '@drizzle/react-plugin';
import { useParams, Link } from "react-router-dom";

import SoyAdmin from "../roles/SoyAdmin"
import SoyCompany from "../roles/SoyCompany"

const { useDrizzle } = drizzleReactHooks;

const Traductortip = () => {
    const { useCacheCall } = useDrizzle();
    let { addr } = useParams();
    const datos = useCacheCall("Traductores", "datosHolders", addr);
    const tokens = useCacheCall("GovernanceToken", "balanceOf", addr);

    // State para el número ingresado en la casilla
    const [inputNumber, setInputNumber] = useState(1);

    // Función para manejar el cambio en la casilla de entrada
    const handleInputChange = (event) => {
        const value = parseInt(event.target.value, 10);
        setInputNumber(value);
    };

    // Función para desbloquear el botón
    const isButtonEnabled = inputNumber >= 1 && inputNumber <= 50;

    return (
        <>


            {/* Casilla de entrada */}
            <label>
                Ingrese un número del 1 al 50:
                <input type="number" value={inputNumber} onChange={handleInputChange} min={1} max={50} />
            </label>

            {/* Botón desbloqueado si se ingresa un número válido */}
            {isButtonEnabled && (
                <button
                    onClick={() => {
                        // Aquí puedes agregar la lógica que necesitas cuando se presiona el botón
                    }}
                >
                    Mi Botón Desbloqueado
                </button>
            )}



            <Link to="/traductores">Volver</Link>
        </>
    );
};

export default Traductortip;
