import { drizzleReactHooks } from '@drizzle/react-plugin'
const { useDrizzle, useDrizzleState } = drizzleReactHooks;

import Watchipfs from './ipfs/watchipfs';

const Cuenta = ({children}) => {

    const {useCacheCall} = useDrizzle();
    const drizzleState = useDrizzleState(state => state);

    const address = drizzleState.accounts[0];
    const balance = drizzleState.accountBalances[address];


    const tokens = useCacheCall("GovernanceToken", "balanceOf", address);
    const admin = useCacheCall("Traductores", "admin");
    const hello = useCacheCall("Hello", "owner");



   return     <>
 <h3>Mis Datos</h3>
            <ul>
                <li>Direcci√≥n: <span style={{color: "blue"}}>{address}</span></li>
                <li>Balance: <span style={{color: "blue"}}>{balance}</span> weis</li>
                <li>Tokens: <span style={{color: "blue"}}>{tokens}</span> weis</li>

                <li>Admin: <span style={{color: "blue"}}>{hello}</span> weis</li>

            </ul>

            <Watchipfs></Watchipfs>
    </>



};
export default Cuenta;
