import React, { useState, useEffect } from 'react';
import { drizzleReactHooks } from '@drizzle/react-plugin';
import { useParams, Link } from 'react-router-dom';

import Daoform from "./Daoform";
import DaoTraductorRow from './DaoTraductorRow';
const { useDrizzle, useDrizzleState } = drizzleReactHooks;

const Daopropose = ({ children }) => {
  const { useCacheCall, useCacheSend } = useDrizzle();
  const drizzleState = useDrizzleState((state) => state);

  const addr = drizzleState.accounts[0];
  const balance = drizzleState.accountBalances[addr];

  // Create an array of 4 options
  const options = ['Option 1', 'Option 2', 'Option 3', 'Option 4'];

  const [selectedOption, setSelectedOption] = useState(null);
  const [selectedNumber, setSelectedNumber] = useState(1);
  const [textInput, setTextInput] = useState('');
  const [addressInput, setAddressInput] = useState('');
  const [isButtonActive, setButtonActive] = useState(false);

  ////////////////////////////////////////////////
  ////////////////////////////////////////////////


  const renderDaoTraductorRows = () => {
    let rows = [];

    for (let i = 0; i < getTraductorLength; i++) {
      rows.push(<DaoTraductorRow key={"ab-" + i} TraductorIndex={i} />);
    }

    return rows;
  };
  ////////////////////////////////////////////////
  ////////////////////////////////////////////////

  const checkButtonActivation = () => {
    setButtonActive(textInput !== '' && addressInput !== '');
  };

  useEffect(() => {
    checkButtonActivation();
  }, [textInput, addressInput]);

  const handleOptionChange = (option) => {
    setSelectedOption(option);
    setSelectedNumber(1); // Reset the selectedNumber when a new option is selected
  };

  const handleNumberChange = (event) => {
    setSelectedNumber(event.target.value);
  };

  const handleTextInputChange = (event) => {
    setTextInput(event.target.value);
  };

  const handleAddressInputChange = (event) => {
    setAddressInput(event.target.value);
  };

  const handleButtonClick = () => {
    console.log("Texto:", textInput);
    console.log("Dirección:", addressInput);
  };

  return (
    <>
      <h3>Votar {selectedOption || 'Seleccione una opción'}</h3>
      <table>
        <thead>
          <tr>
            <th>Option</th>
            <th>Vote Count</th>
          </tr>
        </thead>
        <tbody>
          {options.map((option, i) => (
            <tr key={i}>
              <td>
                <label>
                  <input
                    type="radio"
                    value={option}
                    checked={selectedOption === option}
                    onChange={() => handleOptionChange(option)}
                  />
                  {option}
                </label>
              </td>
              <td>
                {selectedOption === option && option === 'Option 3' && ( // Only show the dropdown for Option 3
                  <select value={selectedNumber} onChange={handleNumberChange}>
                    <option value={1}>1</option>
                    <option value={2}>2</option>
                    <option value={3}>3</option>
                  </select>
                )}
                {selectedOption === option && option === 'Option 4' && ( // Only show the dropdown for Option 4
                  <select value={selectedNumber} onChange={handleNumberChange}>
                    <option value={1}>1</option>
                    <option value={2}>2</option>
                    <option value={3}>3</option>
                  </select>
                )}
              </td>
            </tr>
          ))}
        </tbody>
      </table>


      {/* Formulario */}
      <div>
        <label htmlFor="textInput">Texto:</label>
        <input
          type="text"
          id="textInput"
          value={textInput}
          onChange={handleTextInputChange}
        />
      </div>

      {/* Dirección (Address) */}
      <div>
        <label htmlFor="addressInput">Dirección (Address):</label>
        <input
          type="text"
          id="addressInput"
          value={addressInput}
          onChange={handleAddressInputChange}
        />
      </div>

      {/* Botón */}
      <button onClick={handleButtonClick} disabled={!isButtonActive}>Enviar</button>

      {/* Render DaoTraductorRow components */}
      {getTraductorLength > 0 && (
        <>
          <p>Traductoreslength: {getTraductorLength}</p>
          {renderDaoTraductorRows()}
        </>
      )}
      <Daoform Daooption={selectedOption} />
    </>
  );
};

export default Daopropose;
